// TextContent.jsx
import React from 'react';
import '@spectrum-web-components/textfield/sp-textfield.js';
import '../css/TextContent.css'; // Import custom CSS

function TextContent() {
  return (
    <div style={{ display: 'flex', flexWrap: 'wrap', padding: '20px' }}>
      <div className="custom-textfield">
        <sp-field-label for="story-4">Write your Prompt  :</sp-field-label>
        <sp-textfield
          id="story-4"
          multiline
          placeholder="Enter your name"
          value="By default the text area has a fixed height and will scroll when text entry goes beyond the available space. With the use of the `grows` attribute the text area will grow to accommodate the full content of the element."
          style={{ width: '200%', minHeight: '0%' }} // Adjust width and height as needed
        ></sp-textfield>
      </div>
    </div>
  );
}

export default TextContent;
